var operations = require('./operations'),
    execute = require('./execute');

module.exports = function (configuration) {
    // return {
    //     table: function
    // }
    return {
        read: function (query) {
            return execute(configuration, operations.read(query));
        },
        update: function (table, item) {
            return execute(configuration, operations.update(table, item));
        },
        insert: function (table, item) {
            return execute(configuration, operations.insert(table, item));
        },
        delete: function (table, id, version) {
            return execute(configuration, operations.delete(table, id, version));
        },
        truncate: function (table) {
            return execute(configuration, operations.truncate(table));
        }
    }
}
