var helpers = require('../helpers');

module.exports = function (tableConfig) {
    var tableName = helpers.formatTableName(tableConfig.table, tableConfig.schema || 'dbo'),
        pkName = 'PK_' + tableConfig.table;

    return {
        sql: 'CREATE TABLE ' + tableName + ' (id varchar(50) NOT NULL) ON [PRIMARY]; ALTER TABLE ' + tableName + ' ADD CONSTRAINT ' + pkName + ' PRIMARY KEY CLUSTERED (id) ON [PRIMARY]'
    };
}
