var helpers = require('../helpers');

module.exports = function (tableConfig) {
    var tableName = helpers.formatTableName(tableConfig.schema || 'dbo', tableConfig.name),
        pkName = 'PK_' + tableConfig.name;

    return {
        sql: 'CREATE TABLE ' + tableName + ' (id NVARCHAR(50) NOT NULL) ON [PRIMARY]; ALTER TABLE ' + tableName + ' ADD CONSTRAINT ' + pkName + ' PRIMARY KEY CLUSTERED (id) ON [PRIMARY]'
    };
}
