var promises = require('azure-mobile-apps.core/promises');

// execute the requested operation and attach the results to res
module.exports = function (operations) {
    return function (req, res, next) {
        var verb = req.method.toLowerCase(),
            context = constructOperationContext();

        if (operations && operations[verb]) {
            var results = operations[verb](constructOperationParameter(), context);

            if (promises.isPromise(results))
                results.then(setResults, setError);
            else
                setResults(results);
        } else {
            context.execute().then(setResults, setError);
        }

        function constructOperationContext() {
            return {
                execute: function () {
                    return promises.resolved([{ id: 1, text: 'test...' }]);
                }
            }
        }

        function constructOperationParameter() {
            switch(verb) {
                case 'get':
                    return req.query;
                case 'patch':
                case 'post':
                    return req.item;
                case 'delete':
                    return req.query.id;
            }
        }

        function setResults(results) {
            res.results = results;
            next();
        }

        function setError(error) {
            next(error);
        }
    };
}
