// execute the requested operation and attach the results to res
module.exports = function (req, res, next) {
    var table = req._table,
        verb = req.method.toLowerCase(),
        context = constructOperationContext();

    if (table.operation[verb])
        res.results = table.operation[verb](req.query, context);
    else
        res.results = [{ id: 1, text: 'test...' }];

    next();
};
