// translate http requests into a data operation
module.exports = {
    get: function (req, res) {
        var context = req.azureMobile;
        return context.data.read(context.query);
    },
    post: function (req, res) {
        var context = req.azureMobile;
        return context.data.insert(context.table, context.item)
    },
    patch: function (req, res) {
        var context = req.azureMobile;
        return context.data.update(context.table, context.item)
    },
    delete: function (req, res) {
        var context = req.azureMobile;
        return context.data.delete(context.table, context.id, context.version)
    }
}
