var table = require('azure-mobile-apps.express').table;
 
module.exports = {
    softDelete: true,
    undelete: true,
    
    // the middleware property executes supplied middleware for all operations - table.execute represents the middleware defined for each operation
    middleware: [table.execute, log],
    
    // the operation function generates middleware that will execute the provided function with a (query, context) signature
    read: table.operation(function (query, context) {
        return context.execute().then(function (results) {
            return results.forEach(function (item) {
                item.loaded = new Date();
            });
        });
    }),
    
    // we can pass an array of middleware to execute
    insert: [authorise, table.operation(function (item, context) {
            return context.execute().then(function () {
                log.debug('New record inserted - ', JSON.stringify(item));
            });
        })],
    
    // not passing a function to executeXxx will execute the operation with default behavior
    update: [authorise, table.operation(), log]
    
    // omitting an operation altogether will result in default behavior
    //delete: [table.operation()]
}

function log(req, res, next) { }
function authorise(req, res, next) { }