var expect = require('chai').expect,
    tables = require('azure-mobile-apps.express/tables');

describe('azure-mobile-apps.express.tables', function() {
    it("imports raw table configuration", function () {
        var config = tables({ basePath: __dirname });
        config.import('../configuration/tables/');
        expect(config.configuration.raw).to.not.be.undefined;
    })
})
