var expressions = require('../query/expressions'),
    ODataParser = require('../query/ODataParser'),
    core = require('../core'),
    equal = require('assert').equal;

describe('azure-mobile-apps.query.query.ODataParser', function () {
    it("basic parser test", function () {
        var expression = "(user eq 'mathewc')";
        var parser = new ODataParser(expression);
        var result = parser.parse();

        equal(result.expressionType, 'Equal');
        equal(result.left.member, 'user');
        equal(result.right.value, 'mathewc');
    });

    // bug 740863: backslash should not be escape character
    it("parse backslash", function () {
        var expression = "(user eq 'hasan\\')";
        var parser = new ODataParser(expression);
        var result = parser.parse();

        equal(result.expressionType, 'Equal');
        equal(result.left.member, 'user');
        equal(result.right.value, 'hasan\\');
    });

    it("parse positive / negative numbers", function () {
        var result = new ODataParser('(val eq -1.5)').parse();
        equal(result.right.value, -1.5);

        result = new ODataParser('(val eq -1)').parse();
        equal(result.right.value, -1);

        result = new ODataParser('(val eq 1)').parse();
        equal(result.right.value, 1);

        result = new ODataParser('(val eq 1.5)').parse();
        equal(result.right.value, 1.5);
    });

    it("constant expression test", function () {
        var constExpr = new expressions.ConstantExpression('hello');
        equal(constExpr.value, 'hello');
        equal(constExpr.expressionType, 'Constant');
    });

    it("basic expression tree test", function () {
        var p = new expressions.ParameterExpression();
        var left = new expressions.MemberExpression(p, 'user');
        var right = new expressions.ConstantExpression('mathewc');
        var binExpr = new expressions.BinaryExpression(left, right, 'Equal');

        equal(binExpr.left.member, 'user');
        equal(binExpr.right.value, 'mathewc');
        equal(binExpr.expressionType, 'Equal');
    });

    // verify that the odata literal type suffixes are parsed correctly, even
    // though the values actually map to the same number type
    it("numeric literal parsing", function () {
        var testCases = [
            // long literal
            { expr: '1234', value: 1234 },
            { expr: '1234L', value: 1234 },
            { expr: '1234l', value: 1234 },

            // decimal literal
            { expr: '1234M', value: 1234 },
            { expr: '1234.56M', value: 1234.56 },
            { expr: '1234.56m', value: 1234.56 },

            // single literal
            { expr: '1234F', value: 1234 },
            { expr: '1234.56f', value: 1234.56 },
            { expr: '1234.56f', value: 1234.56 },

            // double literal
            { expr: '1234D', value: 1234 },
            { expr: '1234.56d', value: 1234.56 },
            { expr: '1234.56D', value: 1234.56 }
        ];

        for (var i = 0; i < testCases.length; i++) {
            var testCase = testCases[i];
            var expr = new ODataParser(testCase.expr).parse();
            equal(expr.expressionType, 'Constant');
            equal(expr.value, testCase.value);
        }
    });

    it("string replace - nested calls disallowed", function () {
        var expectedExceptionCaught = false;
        try {
            new ODataParser("replace(replace(name, 'x', 'y'), 'x', 'y') eq 'ApplePie'").parse();
        }
        catch (e) {
            expectedExceptionCaught = true;
            equal(e.message, "Calls to 'replace' cannot be nested. (at index 8)");
        }
        equal(expectedExceptionCaught, true);
    });

    it("string replace - replace arg must be constant with valid length", function () {
        var expectedExceptionCaught = false;
        try {
            // try to specify a non-constant for the replace value
            new ODataParser("replace(name, 'x', nonConstant) eq 'ApplePie'").parse();
        }
        catch (e) {
            expectedExceptionCaught = true;
            equal(e.message, "The third parameter to 'replace' must be a string constant less than 100 in length. (at index 32)");
        }
        equal(expectedExceptionCaught, true);

        expectedExceptionCaught = false;
        try {
            // try to specify a string greater than 100 characters in length
            new ODataParser("replace(name, 'x', 'xxxxxxxxxxxxxxxxxxxxxxxxxxxxxxxxxxxxxxxxxxxxxxxxxxxxxxxxxxxxxxxxxxxxxxxxxxxxxxxxxxxxxxxxxxxxxxxxxxxxx') eq 'ApplePie'").parse();
        }
        catch (e) {
            expectedExceptionCaught = true;
            equal(e.message, "The third parameter to 'replace' must be a string constant less than 100 in length. (at index 124)");
        }
        equal(expectedExceptionCaught, true);
    });

    it("parse error double quotes", function () {
        var expression = 'user eq "mathewc"';

        // try a parse failure case
        var expectedExceptionCaught = false;
        try {
            new ODataParser(expression).parse();
        }
        catch (e) {
            expectedExceptionCaught = true;
            equal(e.message, "Syntax error '\"' (at index 8)");
        }
        equal(expectedExceptionCaught, true);
    });
})
