var configurationLoader = require('zumo.core/configurationLoader');

module.exports = function (configuration) {
    configuration.api = configuration.api || {};

    return {
        add: function (tableNameOrPath, definition) {
            if (definition)
                configuration.api[tableNameOrPath] = definition;
            else
                configurationLoader.loadPath(configuration.api, tableNameOrPath, configuration.basePath);
        }
    };
}