var LogStream = require('./LogStream'),
    user = new LogStream(),
    system = new LogStream();

user.system = system;

user.initialise = function (loggingConfiguration) {
    loggingConfiguration = loggingConfiguration || {};
    user.pipe(loggingConfiguration.userStream || process.stdout);
    system.pipe(loggingConfiguration.systemStream || process.stdout);
};

module.exports = user;