var promises = require('zumo.core/promises'),
    expect = require('chai').expect;

describe('zumo.core.promises', function () {
    describe('resolved', function () {
        it("returns a promise already resolved with the specified value", function () {
            return promises.resolved('test')
                .then(function (value) {
                    expect(value).to.equal('test');
                });
        });
    });
})
